import {createMuiTheme} from '@material-ui/core';

const theme = createMuiTheme({
  breakpoints: {
    values: {
      xs: 0,
      sm: 480,
      md: 576,
      lg: 768,
     
    },
  }});
  theme.typography.h3 = {
    fontSize: '6 rem',
    '@media (min-width:600px)': {
     
    },
    [theme.breakpoints.up('md')]: {
      fontSize: '2.4rem',
    },
  };
export default theme;
