import {createMuiTheme} from '@material-ui/core';

const theme = createMuiTheme({
  breakpoints: {
    values: {
      xs: 0,
      sm: 480,
      md: 576,
      lg: 768,
     
    },
  }});
  theme.typography.h1 = {
    
    '@media (min-width:6px)': {
      fontSize: '1.5rem',
    },
    [theme.breakpoints.up('md')]: {
      fontSize: '2.4rem',
    },
  };
export default theme;
